using Carter;
using Mapster;
using MediatR;
using static Basket.API.ShoppingBasket.Cart.RetrieveCartOperation;

namespace Basket.API.ShoppingBasket.Cart;

public class RetrieveCartEndpoint : ICarterModule
{
    public void AddRoutes(IEndpointRouteBuilder app)
    {
        app.MapGet("/cart/{accountName}", async([AsParameters] RetrieveCartRequest request, ISender sender, CancellationToken ct) =>
        {
            var query = new RetrieveCartQuery(request.AccountName);
            var result = await sender.Send(query, ct);
            var response = result.Adapt<RetrieveCartResponse>();
            return Results.Ok(response);
        })
        .WithName("RetriveCartEndpoint")                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      
        .Produces<RetrieveCartResponse>(StatusCodes.Status200OK)
        .ProducesProblem(StatusCodes.Status400BadRequest)
        .WithSummary("Получение корзины")
        .WithDescription("Возвращает корзину пользователя по имени аккаунта");
    }
}
